import useInput from "../hooks/use-input";

const isNotEmpty = value => value.trim() !== '';
const isEmail = value => value.includes('@syna.ch');
const isValidDate = value => value.toString() >= LocalDate.toString(); 

const EntryFormGW = (props) => {
  const {
    value: dateOfEntryValue,
    isValid: dateOfEntryIsValid,
    hasError: dateOfEntryHasError,
    valueChangeHandler: dateOfEntryChangeHandler,
    inputBlurHandler: dateOfEntryBlurHandler,
    reset: resetdateOfEntry,
  } = useInput(isNotEmpty);

  const {
    value: firstNameValue,
    isValid: firstNameIsValid,
    hasError: firstNameHasError,
    valueChangeHandler: firstNameChangeHandler,
    inputBlurHandler: firstNameBlurHandler,
    reset: resetFirstName,
  } = useInput(isNotEmpty);

  const {
    value: lastNameValue,
    isValid: lastNameIsValid,
    hasError: lastNameHasError,
    valueChangeHandler: lastNameChangeHandler,
    inputBlurHandler: lastNameBlurHandler,
    reset: resetLastName,
  } = useInput(isNotEmpty);

  const {
    value: mailValue,
    isValid: mailIsValid,
    hasError: mailHasError,
    valueChangeHandler: mailChangeHandler,
    inputBlurHandler: mailBlurHandler,
    reset: resetMail,
  } = useInput(isEmail);

  let formIsValid = false;

  if (firstNameIsValid && lastNameIsValid && mailIsValid) {
    formIsValid = true;
  }

  const dateOfEntryClasses = dateOfEntryHasError ? 'form-conttol invalid' : 'form-control';
  const firstNameClasses = firstNameHasError ? 'form-control invalid' : 'form-control';
  const lastNameClasses = lastNameHasError ? 'form-control invalid' : 'form-control';
  const mailClasses = mailHasError ? 'form-control invalid' : 'form-control';

  const submitHandler = event => {
    event.preventDefault();

    if (!formIsValid) {
      return;
    }

    console.log('Submitted!');
    console.log(firstNameValue, lastNameValue, mailValue);

    resetdateOfEntry();
    resetFirstName();
    resetLastName();
    resetMail();
  };

  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className={dateOfEntryClasses}>
          <label>Eintrittsdatum</label>
          <input 
            type="date" 
            id="dateOfEntry"
            value={dateOfEntryValue}
            onChange={dateOfEntryChangeHandler}
            onBlur={dateOfEntryBlurHandler}
          />
        </div>     
        <div className={firstNameClasses}>
          <label htmlFor='name'>Vorname</label>
          <input 
            type='text' 
            id='name' 
            value={firstNameValue} 
            onChange={firstNameChangeHandler} 
            onBlur={firstNameBlurHandler}
          />
          {firstNameHasError && <p className='error-text'>Bitte Vorname eingeben.</p>}
        </div>
        <div className={lastNameClasses}>
          <label htmlFor='name'>Last Name</label>
          <input 
            type='text' 
            id='name' 
            value={lastNameValue} 
            onChange={lastNameChangeHandler} 
            onBlur={lastNameBlurHandler} 
          />
          {lastNameHasError && <p className='error-text'>Bitte Nachname eingeben.</p>}          
        </div>
      </div>
      <div className={mailClasses}>
        <label htmlFor='name'>E-Mail Address</label>
        <input 
            type='email' 
            id='email' 
            value={mailValue} 
            onChange={mailChangeHandler} 
            onBlur={mailBlurHandler}
        />
        {mailHasError && <p className='error-text'>Bitte gültige Mailadresse eingeben.</p>}        
      </div>
      <div className='form-actions'>
        <button>Zurück</button>
        <button disabled={!formIsValid}>Senden</button>
      </div>
    </form>
  );
};

export default EntryFormGW;
