import { useState } from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== '';
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);
  };

  const nameInputBlurHandler = event => {
    setEnteredNameTouched(true);
  };

  const formSubmissionHandler = event => {
    event.preventDefault();

    setEnteredNameTouched(true);

    if (!enteredNameIsValid) {
      return;
    }

    setEnteredNameIsValid(true);

    console.log(enteredName);

    setEnteredName('');
  };

  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        {/*         <label htmlFor='dateOfEntry'>Datum</label>
        <input type='date' id='dateOfEntry' />
        <label htmlFor='title'>Anrede</label>
        <select id='title' name='title'>
          <option value="Frau">Frau</option>
          <option value="Herr">Herr</option>
        </select> */}
        <label htmlFor='name'>Name</label>
        <input type='text' id='name' onChange={nameInputChangeHandler} onBlur={nameInputBlurHandler} value={enteredName} />
        {nameInputIsInvalid && <p className="error-text">Name must not be emtpy.</p>}
        {/*         <label htmlFor='surname'>Vorname</label>
        <input type='text' id='surname' /> */}
      </div>
      <div className="form-actions">
        <button>Senden</button>
      </div>
    </form>
  );
};

export default SimpleInput;
