import { useState } from 'react';

import useInput from '../hooks/use-input';

const SimpleInput = (props) => {
  const { 
    value: enteredName, 
    isValid: enteredNameIsValid,
    hasError: nameInputHasError, 
    valueChangeHandler: nameChangedHandler, 
    inputBlurHandler: nameBlurHandler, 
    reset: resetNameInput,
  } = useInput( value => value.trim() !== '');

  const [enteredMail, setEnteredMail] = useState('');
  const [enteredMailTouched, setEnteredMailTouched] = useState(false);

  const enteredMailIsValid = enteredMail.includes('@syna.ch');
  const mailInputIsInvalid = !enteredMailIsValid && enteredMailTouched;

  let formIsValid = false;

  if (enteredNameIsValid && enteredMailIsValid) {
    formIsValid = true;
  }

  const mailInputChangeHandler = event => {
    setEnteredMail(event.target.value);
  };

  const mailInputBlurHandler = event => {
    setEnteredMailTouched(true);
  };

  const formSubmissionHandler = event => {
    event.preventDefault();

    setEnteredMailTouched(true);

    if (!enteredNameIsValid && !enteredMailIsValid) {
      return;
    }

    console.log(enteredName);
    console.log(enteredMail);

    resetNameInput();
    setEnteredMail('');
    setEnteredMailTouched(false);
  };

  const nameInputClasses = nameInputHasError 
    ? 'form-control invalid' 
    : 'form-control';

  const mailInputClasses = mailInputIsInvalid 
    ? 'form-control invalid' 
    : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Name</label>
        <input
          type='text'
          id='name'
          onChange={nameChangedHandler}
          onBlur={nameBlurHandler}
          value={enteredName}
        />
        {nameInputHasError && (
          <p className='error-text'>Kein Name eingegeben.</p>
        )}

      </div>
      <div className={mailInputClasses}>
        <label htmlFor='mail'>E-Mail</label>
        <input
          type='email'
          id='email'
          onChange={mailInputChangeHandler}
          onBlur={mailInputBlurHandler}
          value={enteredMail}
        />
        {mailInputIsInvalid && (
          <p className='error-text'>E-Mailadresse inkorrekt / fehlt.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Senden</button>
      </div>
    </form>
  );
};

export default SimpleInput;
