import { useState } from "react";

// Eigener Hook zur Auswertung des Inputs der Formularfelder und Reset auf leer nach Absenden

const useInput = (validateValue) => {
    const [enteredValue, setEnteredValue] = useState('');
    
    const valueIsValid = validateValue(enteredValue);
    const hasError = !valueIsValid && isTouched;

    const valueChangeHandler = event => {
        setEnteredValue(event.target.value);
    };
    
    const reset = () => {
        setEnteredValue('');
        setIsTouched(false);
    }

    return {
        value: enteredValue, 
        isValid: valueIsValid,
        hasError, // fehlerhafter Input
        valueChangeHandler, // Ver채nderung am Objekt
        reset, // Eingabewerte zur체cksetzen nach Absenden
    };
};

export default useInput;
