import React, {useState} from "react";
import EntryFormGW from './components/EntryFormGW'
import EntryFormALK from './components/EntryFormALK'
import ExitForm from './components/ExitForm'

function App(props) {
  const [isEditing, setIsEditing] = useState(false);

  const startEntryGWHandler = () => {
    setIsEditing(true);
  }

  const stopEntryGWHandler = () => {
    setIsEditing(false);
  }

  return (

    <div className="app">

      {!isEditing && 
      <div className = "form-actions-first">
        <button onClick={startEntryGWHandler}>Eintritt GW</button>
        <button>Eintritt ALK</button>
        <button>Austritt</button>
      </div>}

      {isEditing && <EntryFormGW onCancel={stopEntryGWHandler}/>}
      <EntryFormALK />
      <ExitForm />
    </div>
  );
}

export default App;
